import React, { useState } from 'react';
import axios from 'axios';

const FetchAnnouncementsForm = () => {
  const [company, setCompany] = useState('');
  const [year, setYear] = useState('');
  const [exchange, setExchange] = useState('SGX'); // Default to SGX

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Adjust the API URL or parameters according to your backend implementation
      const response = await axios.get(`/fetch-announcements?company=${company}&year=${year}&exchange=${exchange}`);
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>
          Exchange:
          <select value={exchange} onChange={(e) => setExchange(e.target.value)}>
            <option value="SGX">SGX</option>
            <option value="ASX">ASX</option>
          </select>
        </label>
      </div>
      <div>
        <label>
          Company Name:
          <input
            type="text"
            value={company}
            onChange={(e) => setCompany(e.target.value)}
            placeholder="Company Name"
          />
        </label>
      </div>
      <div>
        <label>
          Year:
          <input
            type="text"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            placeholder="Year"
          />
        </label>
      </div>
      <button type="submit">Fetch Announcements</button>
    </form>
  );
};

export default FetchAnnouncementsForm;
