// InteractiveAnnotatedImage.js
function InteractiveAnnotatedImage({ imagePath, blobs }) {
    return (
        <div style={{ position: 'relative', display: 'inline-block' }}>
            <img src={imagePath} alt="Annotated Invoice" style={{ maxWidth: '100%' }} />
            <svg style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', pointerEvents: 'none' }}>
                {blobs.map((blob, index) => {
                    const { x, y, width, height } = blob.coordinates;
                    return (
                        <rect key={index} x={x} y={y} width={width} height={height}
                            fill="none" stroke="red" strokeWidth="2"
                            pointerEvents="all" // Enable pointer events for rects
                            onClick={() => console.log(`Box ${index} clicked`)} />
                    );
                })}
            </svg>
        </div>
    );
}


export default InteractiveAnnotatedImage;
