// LabelVariationsModal.js
import React, { useState } from 'react';

function LabelVariationsModal({ isOpen, onSave, onCancel }) {
    const [variations, setVariations] = useState('');

    const handleSave = () => {
        onSave(variations.split(',').map(v => v.trim()));
        setVariations(''); // Reset for next use
    };

    if (!isOpen) return null;

    return (
        <div className="modal">
            <input
                type="text"
                value={variations}
                onChange={(e) => setVariations(e.target.value)}
                placeholder="Enter variations, separated by commas"
            />
            <button onClick={handleSave}>Save</button>
            <button onClick={onCancel}>Cancel</button>
        </div>
    );
}

export default LabelVariationsModal;
