import React, { useState } from 'react';
import axios from 'axios';
import UploadForm from './UploadForm'; // Ensure path is correct
import FetchAnnouncementsForm from './FetchAnnouncementsForm'; // Ensure path is correct

function InteractiveAnnotatedImage({ imagePath }) {
    const [selections, setSelections] = useState([]);

    const handleImageClick = (event) => {
        const rect = event.target.getBoundingClientRect();
        const x = event.clientX - rect.left;
        const y = event.clientY - rect.top;

        setSelections(prevSelections => [...prevSelections, { x, y, width: 100, height: 30 }]);
    };

    return (
        <div style={{ position: 'relative', display: 'inline-block' }} onClick={handleImageClick}>
            <img src={imagePath} alt="Annotated Invoice" style={{ maxWidth: '100%' }} />
            <svg style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', pointerEvents: 'none' }}>
                {selections.map((selection, index) => (
                    <rect key={index} x={selection.x} y={selection.y} width={selection.width} height={selection.height}
                          fill="none" stroke="red" strokeWidth="2" />
                ))}
            </svg>
        </div>
    );
}

function App() {
    const [processedImageFilename, setProcessedImageFilename] = useState('');

    // Simulate obtaining the processed image filename from the backend
    // For demonstration, I'm using a button click to fetch the processed image filename
    const handleFetchProcessedImage = async () => {
        try {
            // Example: Fetch the filename of the processed image
            // Adjust the URL and request method according to your actual backend endpoint
            const response = await axios.get('/api/get-processed-image-filename');
            setProcessedImageFilename(response.data.filename); // Adjust according to your response structure
        } catch (error) {
            console.error("Error fetching processed image filename", error);
        }
    };

    const processedImagePath = processedImageFilename ? `/processed-images/${processedImageFilename}` : '';

    return (
        <div className="App">
            <h1>Audit Software Interface</h1>
            <UploadForm />
            <FetchAnnouncementsForm />
            <button onClick={handleFetchProcessedImage}>Load Processed Image</button>
            {processedImagePath && <InteractiveAnnotatedImage imagePath={processedImagePath} />}
        </div>
    );
}

export default App;
