import React, { useEffect, useRef, useState } from 'react';

function InteractiveAnnotatedImage({ imagePath, blobs, onSelectionComplete }) {
  const imageRef = useRef(null);
  const [scaleFactor, setScaleFactor] = useState(1);
  const [selectedPairs, setSelectedPairs] = useState([]);
  const [currentPair, setCurrentPair] = useState([]);

  useEffect(() => {
    const calculateScaleFactor = () => {
      const naturalWidth = imageRef.current?.naturalWidth;
      const displayWidth = imageRef.current?.clientWidth;
      if (naturalWidth && displayWidth) {
        setScaleFactor(displayWidth / naturalWidth);
      }
    };

    if (imageRef.current) {
      if (imageRef.current.complete) {
        calculateScaleFactor();
      } else {
        imageRef.current.onload = calculateScaleFactor;
      }
    }

    window.addEventListener('resize', calculateScaleFactor);
    return () => window.removeEventListener('resize', calculateScaleFactor);
  }, [imagePath]);

  useEffect(() => {
    onSelectionComplete(selectedPairs);
  }, [selectedPairs, onSelectionComplete]);

  const addSelection = (index) => {
    // Allow selection if it's the second selection of a pair
    if (currentPair.length === 1 && currentPair[0] === index) {
      setSelectedPairs([...selectedPairs, [index, index]]);
      setCurrentPair([]);
      return;
    }

    // If currentPair is empty, add the index
    if (currentPair.length === 0) {
      setCurrentPair([index]);
      return;
    }

    // If currentPair is not empty and different index, add it to complete the pair
    if (currentPair.length === 1 && currentPair[0] !== index) {
      setSelectedPairs([...selectedPairs, [...currentPair, index]]);
      setCurrentPair([]);
    }
  };

  const undoSelection = () => {
    if (currentPair.length > 0) {
      setCurrentPair([]);
    } else if (selectedPairs.length > 0) {
      const updatedPairs = selectedPairs.slice(0, -1);
      setSelectedPairs(updatedPairs);
      // If the last pair had two identical indices, restore one of them to currentPair for re-selection
      if (updatedPairs.length && updatedPairs[updatedPairs.length - 1][0] === updatedPairs[updatedPairs.length - 1][1]) {
        setCurrentPair([updatedPairs[updatedPairs.length - 1][0]]);
      }
    }
  };

  const getLabel = (index) => {
    let label = '';
    selectedPairs.forEach((pair, pairIndex) => {
      if (pair.includes(index)) {
        const isA = pair[0] === index;
        const isB = pair[1] === index;
        if (isA) {
          label += `${pairIndex + 1}A `;
        }
        if (isB && pair[0] !== pair[1]) { // Check if B is a different selection
          label += `${pairIndex + 1}B `;
        } else if (isB && pair[0] === pair[1]) { // Same element selected twice
          label = `${pairIndex + 1}A ${pairIndex + 1}B `;
        }
      }
    });
    return label.trim();
  };

  return (
    <div style={{ position: 'relative', display: 'inline-block' }}>
      <button onClick={undoSelection} style={{ position: 'absolute', right: 0, top: 0, zIndex: 10 }}>Undo</button>
      <img ref={imageRef} src={imagePath} alt="Annotated Invoice" style={{ maxWidth: '100%' }} />
      <svg style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', pointerEvents: 'all' }}>
        {blobs.map((blob, index) => {
          const [x, y, width, height] = blob.coordinates.map(coord => coord * scaleFactor);
          const label = getLabel(index);
          const isSelected = label.length > 0;

          return (
            <g key={index} onClick={() => addSelection(index)}>
              <rect
                x={x} y={y} width={width} height={height}
                fill="none" stroke={isSelected ? 'blue' : 'red'} strokeWidth="2"
                style={{ cursor: 'pointer' }}
              />
              {label && (
                <text
                  x={x + 5} y={y + 20}
                  fill="blue" fontSize="15"
                  style={{ userSelect: 'none', pointerEvents: 'none' }}
                >
                  {label}
                </text>
              )}
            </g>
          );
        })}
      </svg>
    </div>
  );
}

export default InteractiveAnnotatedImage;
